import React from "react"

class Summary extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            item: this.props.item,
        }

        this.handleEdit = this.handleEdit.bind(this)
    }

    handleEdit(e, field) {
        
    }

    render() {
        const empty = this.props.item === null
        const item = this.state.item
        const nonEditableFields = new Set(["id", "name"])

        let details

        if (!empty)
            details = (
                <ul className="details">
                    {Object.keys(item).map(
                        prop => (
                            <li key={prop}>
                                <label>{prop.toUpperCase()}</label>
                                {   
                                    // Check if field is editable
                                    (nonEditableFields.has(prop)) 
                                    ? <input value={item[prop]} onChange={(e) => this.handleEdit(e, prop)} disabled />
                                    : <input placeholder={item[prop]} onChange={(e) => this.handleEdit(e, prop)} />
                                }
                            </li>
                        )
                    )}
                </ul>
            )

        return (
            <fieldset className="workspace-container-child rounded-border">
                <legend><h1>{(empty) ? "DETAILS" : this.props.item.name}</h1></legend>
                {empty ? "No item selected" : details}
            </fieldset>
        )
    }
}

export default Summary
