import React from "react";

class List extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            // Select first item in the list by default if it exists
            selectedItem: (props.items.length > 0) ? props.items[0] : '',
        }

        this.handleSelected = this.handleSelected.bind(this)
    }

    handleSelected(e, item, i) {
        // console.log(this.props.setSelectedItem)
        this.props.setSelectedItem(item, i)
    }

    render () {
        const rows = [], empty = this.props.items.length === 0
        let total = 0
        
        for (let i = 0; i < this.props.items.length; i++) { 
            const item = this.props.items[i];
            const subtotal = item.price * item.qty
            total += subtotal

            rows.push(
                // TODO: Change key for tr since more than one item can contain the same ID
                <tr onClick={(e) => this.handleSelected(e, item, i)} key={item.id} onFocus={(e) => this.handleSelected(e, item, i)}>
                    {Object.keys(item).map( 
                        prop => <td key={prop}>{item[prop]}</td>
                    )}
                    <td>{subtotal}</td>
                </tr>
            )
        }

        if (!empty) 
            rows.push(
                <tr key="last-row">
                    <td colSpan="4"><b>FINAL TOTAL</b></td>
                    <td>{total}</td>
                </tr>
            )

        return (
            <div>
                <table>
  <thead>
    <tr>
      <th>ID</th>
      <th>First name</th>
      <th>Last name</th>
      <th>Email</th>
      <th>Street</th>
      <th>Country</th>
      <th>University</th>
      <th>IBAN</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <td>000001</td>
      <td>Lani</td>
      <td>Ovendale</td>
      <td>lovendale0@w3.org</td>
      <td>7850 Old Shore Drive</td>
      <td>United Kingdom</td>
      <td>University of Plymouth</td>
      <td>BG34 MPVP 8782 88EX H1CJ SC</td>
    </tr>
    </tbody>
    </table>
                {
                    empty 
                    ? <div className="centered-label"><i>No items have been added yet. Start billing by adding a new item.</i></div> 
                    : <></>
                }
            </div>
        ) 
    }
}

export default List
