import React from "react"

class Summary extends React.Component {
    render() {
        const empty = this.props.item === null
        const item = this.props.item
        let details

        if (!empty)
            details = (
                <ul className="details">
                    {/* <li>
                        <label>ID:</label>
                        <input value={item.id} disabled />
                    </li>
                    <li>
                        <label>Rate:</label> 
                        <input value={item.price} />
                    </li> */}
                    {Object.keys(item).map(
                        prop => <li>
                            <label>{prop.toUpperCase()}</label>
                            <input value={item[prop]}/>
                        </li>
                    )}
                </ul>
            )

        return (
            <fieldset className="workspace-container-child rounded-border">
                <legend><h1>{(empty) ? "DETAILS" : this.props.item.name}</h1></legend>
                {empty ? "No item selected" : details}
            </fieldset>
        )
    }
}

export default Summary
