import React from 'react'
import ReactDOM from 'react-dom/client'
import './index.css'

class App extends React.Component {
    render() {
        return (
            <div>
                <h1>Billing System</h1>
                <div>
                    <div className='grid-upper-workspace'>
                        <Invoice />
                        <Summary />
                    </div>
                    <div className='grid-control-panel-child-element'>
                        <ControlPanel />
                    </div>
                </div>
            </div>
        )
    }
}

class Invoice extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            items: [
                {
                    id: '123456789',
                    name: 'POTATOES', 
                    price: 60,
                    qty: 1
                },
                {
                    id: '987456321',
                    name: 'ONIONS',
                    price: 100,
                    qty: 2
                }
            ]
        }
    }

    updateList(item) {
        alert(item.name + ' (' + item.id + ') was added!')
        

        alert(this.items.length)
    }

    render() {
        return (
            <div>
                <NewItem updateList={this.updateList} />
                <List items={this.items} />
            </div>
        )
    }
}

class NewItem extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            id: '',
            name: '',
            qty: '',
        }

        this.handleIdChange = this.handleIdChange.bind(this)
        this.handleNameChange = this.handleNameChange.bind(this)
        this.handleQtyChange = this.handleQtyChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    handleIdChange(e) {
        this.setState({id: e.target.value})
    }

    handleNameChange(e) {
        this.setState({name: e.target.value})
    }

    handleQtyChange(e) {
        this.setState({qty: e.target.value})
    }

    handleSubmit(e) {
        this.props.updateList({
            id: this.state.id,
            name: this.state.name,
            price: 50,  // TODO: Must remove this placeholder price value
            qty: this.state.qty
        })
    }
 
    render() {
        return (
            <div>
                <h2>{this.state.name === '' ? 'Add Item' : this.state.name}</h2>
                <input type='text' placeholder='Product ID' value={this.state.id} onChange={this.handleIdChange}/>
                <input type='text' placeholder='Product Name' value={this.state.name} onChange={this.handleNameChange}/>
                <input type='text' placeholder='Quantity purchased' value={this.state.qty} onChange={this.handleQtyChange}/>
                <input type='submit' value='Done' onClick={this.handleSubmit}/>
            </div>
        )
    }
}

class List extends React.Component {
    render () {
        const rows = []
        for (let index = 0; index < this.props.items.length; index++) { 
            const item = this.props.items[index];
            rows.push(
                <Item id={item.id} name={item.name} price={item.price} qty={item.qty} total={item.price * item.qty} />
            )
        }

        return (
            <div>
                <h2>Invoice</h2>
                <table className='table'>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Price</th>
                        <th>Quantity</th>
                        <th>Subtotal</th>
                    </tr>
                    {rows}
                </table>
            </div>
        ) 
    }
}

class Item extends React.Component {
    render () {
        return (
            <tr>
                <td>{this.props.id}</td>
                <td>{this.props.name}</td>
                <td>{this.props.price}</td>
                <td>{this.props.qty}</td>
                <td>{this.props.total}</td>
            </tr>
        )
    }
}

class Summary extends React.Component {
    render() {
        return (
            <div>
                <h2>Summary</h2>
                <h3>Customer Details</h3>
                <h3>Transaction Details</h3>
            </div>
        )
    }
}

class ControlPanel extends React.Component {
    render() {
        return (
            <div>
                <h1>Control Panel</h1>
                <button>Add Item</button>
                <button>Remove Item</button>
                <button>Complete Transaction</button>
                <button>Cancel Transaction</button>
            </div>
        )
    }
}

// ========================================
  
const root = ReactDOM.createRoot(document.getElementById("root"))
root.render(<App />)
